import React, { useContext } from "react";
import MovieList from './../Movies'
import Styled from 'styled-components'
import { AppSearch } from '../context';

export default function Movies(){
    const context = useContext(AppSearch)
    const {searchValue} = context.states;
    return(
        <div className='py-20 px-14 bg-black h-screen overflow-x-scroll '>
            <h2 className='mb-5 text-white text-2xl mr-14'>My list<br/>
            {searchValue}</h2>
             <div className='flex mx-auto scrollbar'>
            {
                MovieList.filter((movies) => {
                    console.log(searchValue === movies.title)
                    return
                })
