import React, { useContext, useEffect, useState } from "react";
import {Latest, Disney} from './../../Movies'
import Styled from 'styled-components'
import { AppSearch } from './../../context';

export default function Movies(){
    const context = useContext(AppSearch)
    const {searchValue} = context.states;
    const [movies, setMovies] = useState(Latest);
    const [disney, setDisney] = useState(Disney);
    // const [list, setList] = useState(false);
    // const [listCheck, setListCheck] = useState(false);

    useEffect(()=>{
        const newList = [...Latest].filter(data => data.title.toLowerCase().match(searchValue.toLowerCase())!== null)
        const disneyList = [...Disney].filter(data => data.title.toLowerCase().match(searchValue.toLowerCase())!== null)


        setMovies(newList);
        setDisney(newList);
    },[searchValue])
    return(
        <div className='py-20 px-14 bg-black h-screen '>
           <div className="">
            <h2 className='mb-5 text-white text-2xl mr-14'>My list<br/>
                </h2>
                <div className='flex mx-auto scrollbar overflow-x-auto h-52'>
                {
                    movies.map((dataList,i) => {
                    return(
                        <div className='text-center mx-1 py-5' key={i}>
                            <StyledImg 
                                    src={dataList.img}
                                    className=''
                                    alt=''
                                />
                                <h2 className='text-white z-50 relative top-0 mt-5 hidden'>{dataList.title}</h2>
                        </div>
                    )
                })
                }
                </div>
           </div>
            <div>
                {
                    disney.length === 0 ?  <h2></h2> :
                    <h2 className='mt-14 mb-5 text-white text-2xl mr-14'>Disney<br/>
                </h2>
                }
                <div className='flex mx-auto overflow-x-scroll h-52'>
                {
                    movies.map((dataList) => {
                    return(
                        <div className='text-center mx-1 py-5'>
                            <StyledImg 
                                    src={dataList.img}
                                    className=''
                                    alt=''
                                />
                                <h2 className='text-white z-50 relative top-0 mt-5 hidden'>{dataList.title}</h2>
                        </div>
                    )
                })
                }
                </div>
            </div>
        </div>
    )
}

const StyledImg = Styled.img `
    margin-top: 20px;
    max-width: unset;
    width: 200px;
    height: 120px;
    :hover{
        transition: 0.5s;
        transform: scale(1.5);
        object-fit: contain;
    }
    @media (max-width: 768px){
        :hover{
            // transform: none;
            transition: 0.5s;
            overflow: scroll;
            object-fit: contain;
            transform: scale(1.2)
        }
    }
`
