import React from 'react'
import { useState } from 'react';
// import Movies from './Movies'
import {Link} from 'react-router-dom'
// import { FiSearch } from "react-icons/fi";
// import Button from './Button';
const Nav = ({keyword,setKeyword}) => {
    const searchBar = ({width:"20rem",background:"#c3c2c22e", border:"1px black", padding:"0.5rem", color:"white", paddingLeft:"35px"})
    let Links =[
        {name: 'Home',link:'/'},
        {name: 'TV Shows',link:'/shows'},
        {name: 'Movies',link:'/movies'},
        {name: 'Latest',link:'/lates'},
        {name: 'My List',link:'/list'},
    ];
    const [active, setActive] = useState(false);
    const ToggleClass = () => {
        !active?
            setActive(true)
        :
            setActive(false);
    }
    return (
    <div className='shadow-xl w-full relative top-0 left-0 z-20'>
        <div className='xl:flex items-center justify-between flex py-4 sticky xl:px-15 px-0 bg-black opacity-90'>
            <div className='font-bold text-2xl cursor-pointer flex xl:items-center font-[arial] text-white '>
                {/* <span className='text-3xl mr-1 pt-2 icon-ionic'>
                <ion-icon name="logo-ionic"></ion-icon>
                </span> */}
                <h1 className='text-black px-4'>
                    <img
                        src={"./img/logo.png"}
                        alt=''
                        className='w-1/2 mx-auto md:w '
                    />
                </h1>

            <ul className={active
                ?'z-20 w-full my-auto xl:flex xl:items-center xl:pb-0 pb-12 absolute top-20 xl:static xl:z-auto left-0 w-fullxl:w-auto xl:pl-20 pl-0 transition-all duration-500 ease-in'
                :'z-20 w-full my-auto xl:flex xl:items-center xl:pb-0 pb-12 absolute top-20 xl:static xl:z-auto left-0 w-fullxl:w-auto xl:pl-20 pl-0 transition-all duration-500 ease-in hidden'}>
                {
                    Links.map((link)=>(
                        <li key={link.name} className='xl:ml-8 text-xl xl:my-0 xl:text-white hover:text-red-700 xl:bg-transparent bg-black py-5 opacity-90'>
                            <Link to={link.link} className='  duration-500 pl-10 w-full'>{link.name}

                            </Link>
                        </li>
                    ))
                }
                {/* <Button className='items-center'>Get Started</Button> */}
                {/* <Button>Read More</Button> */}
            </ul>
            </div>
            <div className='search-bar hidden xl:flex'>
            <input
                style={searchBar}
                key="random1"
                value={keyword}
                placeholder={"| Search Movie"}
                onChange={(e)=> setKeyword(e.target.value)}
                className="rounded-lg pl-11"
            />
            <ion-icon name="search-outline"></ion-icon>

            </div>
            <div className={ active? `hamburger-container block xl:hidden change`: `hamburger-container block xl:hidden`}  onClick={ToggleClass}>
                <div className="bar1"></div>
                <div className="bar2"></div>
                <div className="bar3"></div>
            </div>
        </div>

    </div>
  )
}

export default Nav
