import React from 'react'
import MovieList from './../Movies'
import Styled from 'styled-components'

export default function Movies(){
    return(
        <div className='py-20 px-14 bg-black h-screen overflow-auto'>
            <h2 className='mb-5 text-white text-2xl'>My list<br/></h2>
             <div className='mx-auto inline'>
            {
                MovieList.map((dataList) => {
                return(
                    <div className='inline-block text-center mx-1'>
                        <StyledImg 
                                src={dataList.img}
                                className='overflow-x-scroll'
                                alt=''
                            />
                            <h2 className='text-white z-50 relative top-0 mt-5 hidden'>{dataList.title}</h2>
                    </div>
                )
            })
            }
            </div>
        </div>
    )
}

const StyledImg = Styled.img `
    width: 18vw;
    height: 20vh;
    margin-top: 20px;
    :hover{
        transition: 0.5s;
        transform: scale(1.2);
        object-fit: contain;
    }

    @media (max-width: 768px){
        :hover{
            // transform: none;
            transition: 0.5s;
            overflow: scroll;
            object-fit: contain;
            transform: scale(1.2)
        }
    }
`
