import React, { useContext, useEffect, useState } from "react";
import {Latest} from './../Movies'
import Styled from 'styled-components'
import { AppSearch } from '../context';
import { useNavigate } from "react-router";
import Slider from 'react-slick'
export default function Movies(){
    const context = useContext(AppSearch)
    const {searchValue} = context.states;
    const [movies, setMovies] = useState(Latest); 

    const navigate = useNavigate()

    useEffect(()=>{
        const newList = [...Latest].filter(data => data.title.toLowerCase().match(searchValue.toLowerCase())!== null)
        setMovies(newList);
        console.log('render');
    },[searchValue])
    return(
        <StyleDiv className='py-20 px-14 bg-black h-screen overflow-x-auto '>
            <h2 className='mb-5 text-white text-2xl mr-14'>My list<br/>
            </h2>
             <div className='flex mx-auto scrollbar overflow-x-auto'>
             <Slider {...settings}>
                {
                    movies.map((dataList,i) => {
                    return(
                        <div onClick={() => {
                            navigate(`/view/${dataList.id}`);
                        }} 
                        className='text-center mx-1 py-5 group relative' key={i}>
                            <StyledImg 
                                    src={dataList.img}
                                    className='thumbnail'
                                    alt=''
                                />
                            <a href={() => {
                                navigate(`/view/${dataList.id}`);
                            }}
                                className='play absolute hidden group-hover:block top-[55%] left-[30%] translate-y-[-50%]' >
                                    <img src='./../img/play.png' alt=''/>
                                </a>
                                <h2 className='text-white z-50 relative top-0 mt-5 hidden'>{dataList.title}</h2>
                        </div>
                    )
                })
                }
            </Slider>
            </div>
        </StyleDiv>
    )
}

const StyledImg = Styled.img `

    margin-top: 20px;
    max-width: unset;
    width: 200px;
    height: 120px;
    :hover,
    :hover .play:hover{
        transition: 0.5s;
        transform: scale(1.5);
        object-fit: contain;

    }
    @media (max-width: 768px){
        :hover{
            // transform: none;
            transition: 0.5s;
            overflow: scroll;
            object-fit: contain;
            transform: scale(1.2)
        }
    }
`
const StyledPlay = Styled.a `
    top: 40%;
    left: 30%;
`

const StyleDiv = Styled.div`
    .thumbnail .play:hover{
        transition: 0.5s;
        transform: scale(1.5);
        object-fit: contain;
    }
    @media (max-width: 768px){
        .thumbnail .play:hover{
            // transform: none;
            transition: 0.5s;
            transform: scale(1.2)
        }
    }
`
