                </div>
                <div className='flex mt-5 items-center'>

                </div>

                <button 
                    className='mt-5 px-10 w-full py-2.5 rounded-sm text-white bg-red-600'
                    onClick={isAuth}
                >
                    Sign In
                </button>
                <h1 className='text-white text-xl'>{}</h1>
            </div>
        </div>
    </>
  )
}

export default Login
