import React from 'react'
import name from './img/name.png'
import Styled from 'styled-components'
import {device} from './typescripts/devices'
// import Movies from './pages/Movies';

export const Title = () => {
  return (
    <div>
        <div className='flex flex-col absolute top-1/4 left-10 xl:left-32 py-12'>
        <Styledimg 
            src={name}
            alt=''
            className='w-1/3 xl:w-1/5'
          />
          <StyleList className='text-gray-400 inline-flex list-none'>
            <li><a href='/year'>2020</a></li>
            <li className='md:bg-red-800 bg-none'><a href='/age'>12+</a></li>
            <li><a href='./time'>1h 55 min</a></li>
            <li><a href='/genre'>Action</a></li>
          </StyleList>
        <div className='text-white break-before-all xl:w-1/3 pt-5 leading-6 md:pr-16 pr-10 xl:pr-0'>
          Mulan is a 2020 American fantasy action drama film produced by Walt Disney Pictures. Directed by Niki Caro from a screenplay by Rick Jaffa, Amanda Silver, Lauren Hynek, and Elizabeth Martin, it is a live-action adaptation of Disney's 1998 film of the same name, itself based on the Chinese folklore story Ballad of Mulan. The film stars Yifei Liu in the title role, alongside Donnie Yen, Tzi Ma, Jason Scott Lee, Yoson An, Ron Yuan, Gong Li, and Jet Li in supporting roles. In the film, Hua Mulan, the eldest daughter of an honored warrior, masquerades as a man to take her ailing father's place during a general conscription to counter the Rouran army in Imperial China. 
        </div>
        <div>
          <button className='py-2 px-5 mt-3 bg-red-700 text-white hover:bg-red-800 transition-all duration-100 ease-in rounded-sm'>
            <ion-icon name="play-outline" className='pt-3 m-auto w-3'></ion-icon>Play
          </button>
          <button className='text-white ml-5 py-2 px-5 bg-black opacity-80 xl:opacity-100 border-gray-100 border-r border-1'>
            <ion-icon name="add-outline"></ion-icon>My List
          </button>
        </div>
      </div>
      {/* <Movies/> */}
    </div>
  )
}
export default Title
const Styledimg = Styled.img`
    top: 28%;
    position: relative;
`
const StyleList = Styled.ul`
    position: relative;
    li{
      padding: 5px;
      margin: 0px 25px;
      font-size: 1.1em;
      cursor: pointer;
    }
    li::after{
      position: absolute;
      content: "|";
      padding-left: 30px;
      color: white;
    }
    @media (max-width: 768px) {
      li{
        margin: 0px 5px;
      }
      li::after{
        content: "";
        padding-left: 15px;
      }
    }
    li:nth-child(2){
      border-radius: 2px;
      padding: 5px 15px;
      color: white;
    }
    li:hover{
      cursor: pointer;
      color: white;
      transition: all ease-in 0.5s;

    }

`
