import React, { useEffect, useState } from 'react'
import Styled, {keyframes} from 'styled-components'
import ListMovies from '../../ Movies/ListMovies'
import Title from './Title'
import ClipLoader from "react-spinners/ClipLoader";

const Landing = () => {
  // let movies = ListMovies[0];
  const [isLoading, setIsloading] = useState(false);

  useEffect(() => {
    setIsloading(true)
      setTimeout(()=>{
        setIsloading(false);
      },1000)
  },[])

  return (

   <div>
     {
       isLoading 
       ?
       <StyledLoader className='h-screen w-screen flex justify-center items-center'>
         <ClipLoader loading color="red;" size={50} className=' scale-in-center'/>
       </StyledLoader>
       :
       <StyledBg className='bg-netflix w-full h-screen'>
          <Title/>
      </StyledBg>


     }

   </div>
  )
}
export default Landing
const pulse = keyframes`
    0% {
      -webkit-transform: scale(0);
      transform: scale(0);
      opacity: 1;
    }
    100% {
      -webkit-transform: scale(1);
      transform: scale(1);
      opacity: 1;
    }
  `
const StyledBg = Styled.div`
  background: url(${ListMovies[0].bg}) no-repeat;
  background-size: cover%;
  .scale-in-center {
    -webkit-animation: ${pulse} 0.5s cubic-bezier(0.250, 0.460, 0.450, 0.940) both;
            animation: ${pulse} 0.5s cubic-bezier(0.250, 0.460, 0.450, 0.940) both;
  }

`
const StyledLoader = Styled.div`
  .scale-in-center{
	  -webkit-animation: scale-out-center 0.5s cubic-bezier(0.550, 0.085, 0.680, 0.530) both;
    animation: scale-out-center 0.5s cubic-bezier(0.550, 0.085, 0.680, 0.530) both;
  }
    @-webkit-keyframes scale-out-center {
    0% {
      -webkit-transform: scale(1);
              transform: scale(1);
      opacity: 1;
    }
    100% {
      -webkit-transform: scale(0);
              transform: scale(0);
      opacity: 1;
    }
  }
  @keyframes scale-out-center {
    0% {
      -webkit-transform: scale(1);
              transform: scale(1);
      opacity: 1;
    }
    100% {
      -webkit-transform: scale(0);
              transform: scale(0);
      opacity: 1;
    }
  }

`
