import React from 'react'
import { useState } from 'react';
// import { FiSearch } from "react-icons/fi";
// import Button from './Button';
const Nav = ({keyword,setKeyword}) => {
    const searchBar = ({width:"20rem",background:"#c3c2c22e", border:"1px black", padding:"0.5rem", color:"white", paddingLeft:"35px"})
    let Links =[
        {name: 'Home',link:'/home'},
        {name: 'TV Shows',link:'/shows'},
        {name: 'Movies',link:'/movies'},
        {name: 'Latest',link:'/lates'},
        {name: 'My List',link:'/list'},
    ];
    const [active, setActive] = useState(false);
    const ToggleClass = () => {
        if(!active){
            setActive(true);
        }
        else{
            setActive(false)
        }
    }
    return (
    <div className='shadow-md w-full fixed top-0 left-0 z-20'>
        <div className='md:flex items-center justify-between flex py-4 sticky md:px-15 px-7 bg-black opacity-90'>
            <div className='font-bold text-2xl cursor-pointer flex md:items-center font-[arial] text-white '>
                {/* <span className='text-3xl mr-1 pt-2 icon-ionic'>
                <ion-icon name="logo-ionic"></ion-icon>
                </span> */}
                <h1 className='text-black px-4'>
                    <img
                        src={"./img/logo.png"}
                        alt=''

                    />
                </h1>

            <ul className='hidden z-20 w-full my-auto md:flex md:items-center md:pb-0 pb-12 absolute top-20 md:static md:z-auto left-0 w-fullmd:w-auto md:pl-20 pl-0 transition-all duration-500 ease-in'>
                {
                    Links.map((link)=>(
                        <li key={link.name} className='md:ml-8 text-xl md:my-0 md:text-white hover:text-red-700 md:bg-transparent bg-black py-5 opacity-90'>
                            <a href={link.link} className='  duration-500 pl-10 w-full'>{link.name}</a>
                        </li>
                    ))
                }
                {/* <Button className='items-center'>Get Started</Button> */}
                {/* <Button>Read More</Button> */}
            </ul>
            </div>
            <div className='search-bar hidden md:flex'>
            <input
                style={searchBar}
                key="random1"
                value={keyword}
                placeholder={"| Search Country"}
                onChange={(e)=> setKeyword(e.target.value)}
                className="rounded-lg pl-11"
            />
            <ion-icon name="search-outline"></ion-icon>

            </div>
            <div className="hamburg-container md:inline-block { active?  }" onClick={ToggleClass}>
                <div className="bar1"></div>
                <div className="bar2"></div>
                <div className="bar3"></div>
            </div>
        </div>

    </div>
  )
}

export default Nav
